import React, {useState, useEffect} from 'react'
import {Link} from 'react-router-dom';
import "./Home.css";
import {toast} from 'react-toastify';
import axios from 'axios';

const Home = () => {
    const [data, setData] = useState([]);
    const loadData = async () => {
        const response = await axios.get("http://localhost:4000/api/get");
        setData(response.data);
    };

    useEffect(() => {
        loadData();
    }, []);

    const deleteContact = (email) => {
        if(window.confirm("Delete the contact?")){
            axios.delete(`http://localhost:4000/api/remove/${email}`);
            toast.success("User deleted");
            setTimeout(() => loadData(), 500);
        }   
    }
    return (
        <div>
            <Link to="/addContact">            
                <button className = "btn btn-contact">Add New User</button>
            </Link>
            <table className = "styled-table">
                <thead>
                    <tr>
                        <th>Email</th>
                        <th>Name</th>
                        <th>Surname</th>
                        <th>Salary</th>
                        <th>Phone</th>
                        <th>Country</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((item) => { //index
                        return (
                            <tr key={item.email}>
                                {/* <th scopre="row">{index+1}</th> */}
                                <td>{item.email}</td>
                                <td>{item.name}</td>
                                <td>{item.surname}</td>
                                <td>{item.salary}</td>
                                <td>{item.phone}</td>
                                <td>{item.cname}</td>
                                <td>
                                    <Link to={`/update/${item.email}`}>
                                     <button className = "btn btn-edit">Edit</button>
                                    </Link>
                                    <button className = "btn btn-delete" onClick={() => deleteContact(item.email)}>Delete</button>
                                </td>
                            </tr>
                        )
                    })}
                </tbody>
            </table>
        </div>
    )
}
export default Home;
